import React, { Component, useEffect, useState } from "react";
import UserTodos from "./UserTodos.js";

function App() {
  const [users, setUsers] = useState([]);
  // solution
  const [currentUser, setCurrentUser] = useState({});
  const [toDos, setTodos] = useState([]);

  useEffect(() => {
    fetch("https://jsonplaceholder.typicode.com/users")
      .then((response) => response.json())
      .then(setUsers)
      .catch((error) => {
        console.log(error);
      });
  }, []);

  // solution
  useEffect(() => {
    if (currentUser.id) {
      fetch(
        `https://jsonplaceholder.typicode.com/users/${currentUser.id}/todos`
      )
        .then((response) => response.json())
        .then(setTodos)
        .catch((error) => {
          console.log(error);
        });
    }
  }, [currentUser]);

  return (
    <div className="App">
      {/* Write your code below */}
      {users.map((user) => (
        <h1 onClick={() => setCurrentUser(user)}>{user.name}</h1>
      ))}
      <UserTodos toDos={toDos} />
    </div>
  );
}

export default App;
